package app.dto.request;import lombok.Data;import javax.validation.constraints.NotBlank;import javax.validation.constraints.Size;@Datapublic class SignUpRequest {    @NotBlank(message = "Username cannot be blank.")    @Size(min = 4, max = 50, message = "Username length must be between 5 and 50 characters.")    private String username;    @NotBlank(message = "Password cannot be blank")    @Size(min = 8, max = 50, message = "Password length must be between 8 and 50 characters.")    private String password;}